package apnaCollefge;

public class ContinueStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n=23;
        for(int i=1;i<=n;i++) {
        	if(i==8) {
        		continue;
        	}
        	if(i==10) {
        		continue;
        	}
        	
        	if(i==13) {
        		continue;
        	}
        	System.out.println(i);
        }
      
	}

}
