package apnaCollefge;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int num=8;
        int fact=1;
        System.out.println("factorial is");
        for(int i=1;i<=num;i++) {
        	fact=fact*i;     	
        	System.out.println(fact);
        }
	}

}
